scheduler._locate_css = function(e, classname, strict){
	if(strict === undefined)
		strict = true;

	var trg = e.target || e.srcElement;
	var css = '';

	while (trg){
		css = scheduler._getClassName(trg);

		if(css){
			var ind = css.indexOf(classname);
			if (ind >= 0){
